package by.tut.ssmt.controller.command.impl;import by.tut.ssmt.controller.ControllerValidator;import by.tut.ssmt.controller.command.AbstractCommand;import by.tut.ssmt.controller.command.Command;import by.tut.ssmt.controller.exception.ControllerException;import by.tut.ssmt.service.ProductService;import by.tut.ssmt.service.ServiceFactory;import by.tut.ssmt.service.exception.NullOrEmptyException;import by.tut.ssmt.service.exception.ServiceException;import javax.servlet.ServletException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.util.logging.Logger;import static by.tut.ssmt.controller.util.ControllerConstants.MESSAGE;import static by.tut.ssmt.controller.util.ControllerConstants.PRODUCT_NAME;public class DeleteCommand extends AbstractCommand implements Command {    private final ServiceFactory serviceFactory = ServiceFactory.getInstance();    private final ProductService productService = serviceFactory.getProductService();    private final ControllerValidator controllerValidator = new ControllerValidator();    private static final Logger LOGGER = Logger.getLogger(DeleteCommand.class.getName());    @Override    public void execute(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException, ControllerException {        try {            final String product = request.getParameter(PRODUCT_NAME);            controllerValidator.isNotNullOrEmpty(product);            boolean result = productService.deleteService(product);            checkOperationForSuccess(request, result);            LOGGER.info("RequestURL: " + request.getRequestURL());            response.sendRedirect("/main?" + MESSAGE + "=" + productService);        } catch (ServiceException | NullOrEmptyException e) {            throw new ControllerException(e.getMessage());        }    }}